import React, { useState } from 'react';
import './Services.css';

const Services = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const handleClick = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const services = [
    {
      title: "Ceza Hukuku Hizmeti",
      description: "Suçlamalarla karşı karşıya kaldığınızda, uzman ceza hukuku avukatlarımız sizin için savunma yapacaktır. Haksız yere suçlanmamanız, adil bir yargılama sürecine girebilmeniz için yanınızdayız."
    },
    {
      title: "Aile Hukuku Hizmeti",
      description: "Boşanma, mal paylaşımı, çocukların velayeti gibi hassas konularda hukuki desteğe mi ihtiyacınız var? Aile hukuku avukatlarımız size rehberlik eder."
    },
    // Diğer servisler...
  ];

  return (
    <section id="services" className="services">
      <h2>Çalışma Alanlarımız</h2>
      <ul>
        {services.map((service, index) => (
          <li
            key={index}
            onClick={() => handleClick(index)}  // Tıklandığında büyüt
            className={activeIndex === index ? 'active' : ''}
          >
            <h3 className="service-title">{service.title}</h3>
            <p
              className="service-description"
              style={{ display: activeIndex === index ? 'block' : 'none' }}
            >
              {service.description}
            </p>
          </li>
        ))}
      </ul>
    </section>
  );
}

export default Services;
