import React from 'react';
import './Blog.css';

const Blog = () => {
  const blogPosts = [
    {
      title: "Yeni Hukuk Düzenlemeleri 2024",
      description: "2024 yılında Türkiye'deki yeni hukuk düzenlemeleri ve bunların bireyler ve işletmeler üzerindeki etkileri hakkında detaylı bir inceleme.",
      date: "2024-12-01"
    },
    {
      title: "Ceza Hukukunda Önemli Değişiklikler",
      description: "Ceza hukuku alanındaki en son gelişmeler ve yapılan değişikliklerin suçluluk ve savunma süreçlerine etkisi.",
      date: "2024-11-25"
    },
    {
      title: "Aile Hukukunda Reform Çalışmaları",
      description: "Aile hukuku alanındaki reformların aile içi anlaşmazlıklarda nasıl bir değişim getirdiğini ve yargı sürecine olan etkilerini keşfedin.",
      date: "2024-11-15"
    }
  ];

  return (
    <section id="blog" className="blog">
      <h2>Blog Yazılarımız</h2>
      <div className="blog-list">
        {blogPosts.map((post, index) => (
          <div key={index} className="blog-post">
            <h3 className="blog-title">{post.title}</h3>
            <p className="blog-description">{post.description}</p>
            <span className="blog-date">{post.date}</span>
            <button className="read-more">Devamını Oku</button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Blog;
