import React, { useEffect } from 'react';
import './Services.css';

const Services = () => {
  useEffect(() => {
    const servicesItems = document.querySelectorAll('.services li');

    const observerOptions = {
      root: null, // viewport
      rootMargin: '0px',
      threshold: 0.5, // öğenin %50'si görünür olduğunda animasyonu başlat
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate'); // Animasyonu başlat
        }
      });
    }, observerOptions);

    servicesItems.forEach((item) => observer.observe(item));

    return () => {
      servicesItems.forEach((item) => observer.unobserve(item)); // Cleanup
    };
  }, []);

  return (
    <section id="services" className="services">
      <h2>Hizmetlerimiz</h2>
      <ul>
        <li>
          <h3>Ceza Hukuku Hizmeti</h3>
          <p>Suçlamalarla karşı karşıya kaldığınızda, uzman ceza hukuku avukatlarımız sizin için savunma yapacaktır...</p>
        </li>
        <li>
          <h3>Aile Hukuku Hizmeti</h3>
          <p>Boşanma, mal paylaşımı, çocukların velayeti gibi hassas konularda hukuki desteğe mi ihtiyacınız var...</p>
        </li>
        {/* Diğer hizmetler... */}
      </ul>
    </section>
  );
};

export default Services;
